import React, { useState } from 'react'
import Todo from './components/Todo'
import FormTodo from './components/FormTodo'
import { container } from "react-bootstrap";
import './App.scss'
function App() {
  // Membuat state
  const [name, setName] = useState([
    {
      text : "Project Todo",
      isDone: false
    }
  ])

  // Membuat Form Baru
  const addForm = (text) => {
    const newAdd = [...name, { text }]
    setName(newAdd)
  }

  // Menandai Form Selesai 
  const markDone = (text) => {
    const newAdd = [...name]
    newAdd[text].isDone = true
    setName(newAdd)
  }

  // Menghapus From
  const removeForm = (index) => {
    const newAdd = [...name]
    newAdd.splice(index, 1)
    setName(newAdd)
  }

  return (
    <div className='app'>
      <div className='container'>
        <div className="mt-3">
          <h3 className="text-center font-w-8">Create Todo App</h3>
        </div>
      </div>
    </div>
  )
}

export default App
